definition.script:
node('master') {
    def configDir = "${WORKSPACE}/configs"
    sh "mkdir -p ${configDir}"

    sh """
cp -a ${HOME}/config.xml ${configDir}
cp -a ${HOME}/jenkins.model.JenkinsLocationConfiguration.xml ${configDir}
cp -a ${HOME}/scriptApproval.xml ${configDir}
cp -a ${HOME}/hudson.tasks.Mailer.xml ${configDir}
cp -a ${HOME}/jp.am1002.plugins.FilteredMailConfiguration.xml ${configDir}
"""

    // jobsにある全てのconfig.xmlをコピーします
    dir("${HOME}/jobs") {
        sh """
for config in \$(find . -type f -name 'config.xml' -o -name 'state.xml')
do
    config_dir=${WORKSPACE}/configs/jobs/\$(dirname \${config})
    mkdir -p \${config_dir}
    cp -a \${config} \${config_dir}
done
find ${WORKSPACE}/configs -type d -name 'branches' | xargs rm -rf
"""
    }

    // jobsディレクトリをtarで保存します
    dir('configs') {
        sh "tar cvf ${WORKSPACE}/configs.tar ."
    }
    archiveArtifacts 'configs.tar'

    // workspaceを削除します
    deleteDir()
}
